arguments.argument:
// Tell all selected units to move to the mouse position
var i;

for(i = 0; i < ds_list_size(selectedUnits); i += 1)
{
    unit = ds_list_find_value(selectedUnits, i);

    if(instance_exists(unit))
    {
        with(unit)
        {
            if(i == 0)
            {         
                event_user(0);
            }
            moveTo(other.x, other.y);
        }
    }
}
